import React from 'react';
import { Icon, List } from 'semantic-ui-react';
import axios from 'axios';
import { connect } from 'react-redux';
import {PropTypes} from 'prop-types';

export class Crudbooks extends React.Component {




    // handleSubmit = (e) => {
    //     e.preventDefault();
    //     this.props.login(this.state.email, this.state.password);
    //   };

// handleClick() {
//     this.props.getBooks();
// }

    render() {
        console.log("boooks" + this.props.books);

         const {books} = this.props.books;
          return (
            <React.Fragment>
                <div>
                    <List celled>
                    {books.slice(0, 10).map(book => (
                        <List.Item>
                            {/* <Image avatar src='/images/avatar/small/helen.jpg' /> */}
                            <List.Content>
                                <List.Header>                          {book.title}

                                     <div style={{marginRight : '50px' }} align="right" >
                                     <button style={{marginRight : '20px' }}>  <Icon name='setting' size='large' /></button> 
                                     <button >  <Icon name='trash alternate' size='large' /></button>
                                    </div>
                                </List.Header>
                                {book.content}

                        </List.Content>
                        </List.Item>
                    ))}
                    </List>

                </div>
            </React.Fragment>
        )
    }
}
// const mapStateToProps = (state) => ({
//     initbook: state,
//   });
const mapStateToProps = (state) => ({
    books: state.books.books,
  });
  
  export default connect(mapStateToProps)(Crudbooks);
