import React from 'react';
import '../../public/css/account.css';
import {Card, Icon, Image} from 'semantic-ui-react';
import {  Row, Col } from 'reactstrap';
import axios from 'axios';
import faker from 'faker';

export class account extends React.Component{
    state = {user:'', sujets:[]}
    componentDidMount(){
        // let {sujet, profil}= this.state;
        //const user = JSON.parse(localStorage.getItem('token'));
        axios.get('http://localhost:3000/api/users/account/'+ this.props.match.params.id)
            .then(     
            res =>( this.setState({ user: res.data }),
            console.log('frikha ',this.props.match.params.id)
            ))
            .catch(err => console.log('erreur..',err))
        axios.get('http://localhost:3000/api/sujets/byuserid/'+ this.props.match.params.id)
            .then(res => (this.setState({ sujets: res.data })))

        
    }

    render(){
         const { user, sujets } = this.state;
    
        return(
            <div className='all'>
                <Row>
                <Col>
                <h1 className='profil'><b>{user.username}'s profile</b></h1><hr/>

                    <Card className='a'>
                        <Image className='cardimg' src={faker.internet.avatar()} wrapped ui={false} />
                        <Card.Content>
                        <Card.Header><br/></Card.Header>
                            <div className='namediv'><h4 className='namediv'>name : </h4><h3 className='namediv'>{ user.firstname } { user.lastname }</h3></div> <br/>
                            <div className='namediv'><h4 className='namediv'>email : </h4><h3 className='namediv'>{ user.email }</h3></div> <br/>
                            <div className='namediv'><h4 className='namediv'>Phone : </h4><h3 className='namediv'>{ user.phone }</h3></div> 

                        </Card.Content>
                    </Card>
                </Col>
                <Col>        
            <h1 className='A'><b>A</b></h1><hr/>
            <h2>blog :</h2>


                {sujets.slice(0,4).map(suj=>(
                <Card size='small' className='a'>
                    <Image src='data:image/png;base64,'
                     wrapped ui={false} />
                    <Card.Content>
                    <Card.Header as='a' href='' onClick={() => this.props.history.push('/affich-sujet/'+suj._id)}>{suj.title}</Card.Header>
                    <Card.Meta>
                        <span className='date'>Added on: {suj.date}</span>
                    </Card.Meta>
                    <Card.Description>{suj.text}
                   </Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                    <p>
                    <Icon name="comment"/>{suj.totalcomments}  replies                  
                    </p>
                    </Card.Content>
                </Card>
            ))}
                </Col>
                <Col>        
            <h1 className='A'><b>A</b></h1><hr/>
                    <h2 className='A'>A</h2>

                {sujets.slice(4,8).map(suj=>(
                <Card size='small' className='a'>
                    <Image src='data:image/png;base64,'
                     wrapped ui={false} />
                    <Card.Content>
                    <Card.Header as='a' href='' onClick={() => this.props.history.push('/affich-sujet/'+suj._id)}>{suj.title}</Card.Header>
                    <Card.Meta>
                        <span className='date'>Added on: {suj.date}</span>
                    </Card.Meta>
                    <Card.Description>{suj.text}
                   </Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                    <p>
                    <Icon name="comment"/>{suj.totalcomments}  replies                  
                    </p>
                    </Card.Content>
                </Card>
            ))}
                </Col>
            </Row>
            
          </div>
        )
    }
}
