import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export class Search extends Component {

    constructor(props) 
    {
      super(props);
      this.state = {
        resultat: {},
        searchBox:''};

       this.handleChange = this.handleChange.bind(this);
       this.handleClick=this.handleClick.bind(this);
    }

    handleChange(event) {
    this.setState({searchBox: event.target.value});
    }

    handleClick(event) {
        console.log(this.state.searchBox);
        axios({
            method: 'get',
            url: 'http://127.0.0.1:5000/pages/searchurl/'+this.state.searchBox
            })
          .then(res => {
            console.log("###########################################################");
            console.log(res.data);
            console.log("###########################################################");
            this.state.resultat=res; 
            console.log(this.state.resultat);

          })
          .catch(
              console.log("\n***************search not working**************************")
                 );

      }


    render() {
        return (
            <div>
                    <div className="header-search-block">
                        <input type="text" placeholder="Search entire store here" value={this.state.searchBox} onChange={this.handleChange} />
                      <Link to={{
                          pathname:"/forum",
                          state:{name:"aya"}
                      }}>


                        <button onClick={this.handleClick}  ><i className="fa fa-search"></i></button>
                        </Link> 
                         </div>
                      {/* <button type="submit"><i class="fa fa-search"></i></button> */}

                </div>
        );
    }
}
