import React from 'react';

import '../App.css';
import {BrowserRouter as Router } from 'react-router-dom';

import {AvisClients} from '../components/avisClients';
import {Ads} from '../components/ads';
import {Challenge} from '../components/challenge';
import {AddBook} from '../components/addBook';
import {UserBooks} from '../components/userBooks';
import {Gallery} from '../components/gallery';
import {BarCategory} from '../components/barCategory';
import {News} from '../components/news';
import {Top20} from '../components/top20';
import {Cover} from '../components/cover';
import {ChallengeAccueil} from '../components/challengeAccueil';





export class Accueil extends  React.Component{
    render(){

      {/*categories+  news  + recommanded  + challenge  + addBook  + Top20  + userBooks/>*/}
{/*    + addBook  + Top20  + userBooks/>*/}   



      return (
    <React.Fragment>
      <div className="container">
        <Router>
        {<Cover/>}
        {<Gallery/>}  


        <div className="row"> 
          <div className="col-sm-3">{<ChallengeAccueil/>}</div>
          <div className="col-sm-9">{<BarCategory/>}</div> {/* livres recommand√©s */}
        </div>  

         
        {<News/>}  

        

        <br/><br/><br/><br/>
  {<BarCategory/>}  {/*TOP20 */} 



        <div className="row"> 
          <div className="col-sm-3">{<AddBook/>}</div>
          <div className="col-sm-9">{<Ads/>}</div> 
        </div>
         
         
         
         
         
         
             <br /><br /><br /><br />
          <h1> Books Category : </h1>      <br /><br /><br /><br />
         
          {<Ads/>}
      
        <div className="row"> 
          <div className="col-sm-3">{<Challenge/>}</div>
          <div className="col-sm-9">{<UserBooks/>}</div> 

        </div> 
          {<AvisClients/>}
         </Router>
      </div>
      </React.Fragment>


  )
    }}



    export default Accueil;
