        <Col md="8" >
          {/*====================================================================================== */}



          <Cmntr />
          {elements.map((value, index) => {
            return <Cmntr />
          })}

















          <Col md="10">  <hr /> </Col>

          {/*====================================================================================== */}
          <p className="text-muted text-center" > <small className="text-muted"><a href="h"> voir plus des commantaires ... </a>    </small> </p>
        </Col>
      </div>
    );
  }
}

export default Forum;
