import React from 'react';
import { Icon, List } from 'semantic-ui-react';
import axios from 'axios';
import { connect } from 'react-redux';
import {PropTypes} from 'prop-types';
import { getBooks } from '../../actions/books-action';

export class Crudbooks extends React.Component {

    state = { books: [] }


    // handleSubmit = (e) => {
    //     e.preventDefault();
    //     this.props.login(this.state.email, this.state.password);
    //   };

// handleClick() {
//     this.props.getBooks();
// }
    componentDidMount() {
      axios.get('http://localhost:8000/api/pages/books/')
        .then(
          res => (this.setState({ books: res.data }),
          console.log(res.data)
          ))
        .catch(err => console.log('erreur..', err));
      this.props.getBooks(res.data);
    }
    render() {
      const {books} = this.state;
          return (
            <React.Fragment>
                <div>
                    <List celled>
                    {this.props.books.slice(0, 10).map(book => (
                        <List.Item>
                            {/* <Image avatar src='/images/avatar/small/helen.jpg' /> */}
                            <List.Content>
                                <List.Header>                          {book.title}

                                     <div style={{marginRight : '50px' }} align="right" >
                                     <button style={{marginRight : '20px' }}>  <Icon name='setting' size='large' /></button> 
                                     <button >  <Icon name='trash alternate' size='large' /></button>
                                    </div>
                                </List.Header>
                                {book.content}

                        </List.Content>
                        </List.Item>
                    ))}
                    </List>

                </div>
            </React.Fragment>
        )
    }
}
const mapStateToProps = (state) => ({
    books: state,
  });

  export default connect(mapStateToProps,{getBooks})(Crudbooks);
