import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Avatar } from '@material-ui/core';
import { AvatarGroup } from '@material-ui/lab';
import Cmntr from '../components/cmntr'

import { Button, Card, CardBody, CardGroup, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row, CardFooter } from 'reactstrap';

const elements = ['c1', 'c2', 'c3', 'c4'];


export class Forum extends Component {






  render() {
    return (
      <div className="pt-5  pb-5" style={{ display: 'flex', justifyContent: 'center' }}>
        <Col md="8" >
          {/*====================================================================================== */}


          <div className="animated fadeIn">

            <Card>
              <CardHeader>
                <strong>Add New commntr</strong>
              </CardHeader>

              <CardBody>
                <Form action="" method="post" encType="multipart/form-data" className="form-horizontal">

                  <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="textarea-input">Description</Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input type="textarea" name="description" id="textarea-input" rows="7" placeholder="book description..." required />
                    </Col>
                  </FormGroup>

                </Form>
              </CardBody>
              <CardFooter>
                <Button type="submit" size="sm" color="primary"><i className="fa fa-dot-circle-o"></i> Submit</Button>
                <Button type="reset" size="sm" color="danger"><i className="fa fa-ban"></i> Reset</Button>
              </CardFooter>
            </Card>

          </div>



          {/*====================================================================================== */}
          {elements.map((value, index) => {
            return <Cmntr />
          })}

          {/*====================================================================================== */}
          <p className="text-muted text-center" > <small className="text-muted"><a href=" "> voir plus des commantaires ... </a>    </small> </p>
        </Col>

        <Col md="2" >
          <a href=" " className="btn btn-primary d-block  cui-user-follow icons   justify-end" >Ajouter un commantaire </a>
        </Col>


      </div>
    );
  }
}

export default Forum;
